'use client';
import { useState, useEffect } from 'react';
import { User } from 'firebase/auth';

export const useUser = (): { user: User | null; loading: boolean } => {
  // Mock implementation of useUser hook
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate user fetching
    const timeout = setTimeout(() => {
        // To test logged out state, set this to null
        // To test logged in state, create a mock user object
        const mockUser = {
            uid: 'mock-user-id',
            displayName: 'Mock User',
            email: 'mock@user.com',
            photoURL: `https://i.pravatar.cc/150?u=mock-user-id`,
        } as User;
      
        // setUser(mockUser); 
        setUser(null);
        setLoading(false);
    }, 1000);

    return () => clearTimeout(timeout);
  }, []);

  return { user, loading };
};
